#!/bin/bash
#
# Using docker-compose-template.yaml, replace constants with private key file names
# generated by the cryptogen tool and output a docker-compose.yaml specific to this
# configuration
function replacePrivateKey () {
  # Copy the template to the file that will be modified to add the private key
  cp docker-compose-template.yaml docker-compose.yaml

  # The next steps will replace the template's contents with the
  # actual values of the private key file names for the two CAs.
  CURRENT_DIR=$PWD
  cd crypto-config/peerOrganizations/pagos.everis.com/ca/
  PRIV_KEY=$(ls *_sk)
  cd "$CURRENT_DIR"
  sed -i "s/PAGOS_CA_PRIVATE_KEY/${PRIV_KEY}/g" docker-compose.yaml
  cd crypto-config/peerOrganizations/cobros.everis.com/ca/
  PRIV_KEY=$(ls *_sk)
  cd "$CURRENT_DIR"
  sed -i "s/COBROS_CA_PRIVATE_KEY/${PRIV_KEY}/g" docker-compose.yaml
}

replacePrivateKey
